// チェッカー
function setup() {
  createCanvas(200, 200);
  const size = width / 8; // マスの一辺の長さ
  noStroke();
  for(let i = 0; i < 8; i++){
    for(let j = 0; j < 8; j++){
      // BLANK[1] ヒント： rectのx座標は size * i, y座標は size * j
    if(i%2!=0&&j%2==0||i%2==0&&j%2!=0)
      rect(size*i,size*j,size,size)
      fill(0)
    }
    if(j<=3){
      fill(1)
      ellipse(size*i,size*j,size,size)
    }
  }
}

// チェッカー
function setup() {
  createCanvas(200, 200);
  const size = width / 8; // マスの一辺の長さ
  noStroke();
  for(let i = 0; i < 8; i++){
    for(let j = 0; j < 8; j++){
      // BLANK[1] ヒント： rectのx座標は size * i, y座標は size * j
    if(i%2!=0&&j%2==0||i%2==0&&j%2!=0){
      fill(100)
      rect(size*i,size*j,size,size)

      if(j<3){
        fill(255, 0, 0)
        ellipse(size*i+size*1/2,size*j+size*1/2,size*0.8,size*0.8)
      }
      
    if(j>=5){
        fill( 0)
        ellipse(size*i+size*1/2,size*j+size*1/2,size*0.8,size*0.8)
      }
    }
  }
  }
}

// ギリシャ国旗（ほかの国旗に挑戦してもOK）
function setup() {
  createCanvas(270, 180);
  noStroke();
  background(255);

  const d = height / 9; // 縞1本の太さ
  const blue = color(0, 51, 160);

  // 縞を描く
  for(let i = 0; i < 9; i++){
    // BLANK[1] ヒント：縞の色を交互に変えるには2で割った余りを使おう
    if(i%2==0){
      fill(blue)
    rect(0, i * d, width,d);
    }
  }

  // 十字を描く
  const size = d * 5;
  fill(blue);
  rect(0, 0, size, size);
  fill(255);
  rect(d * 2, 0, d, size);
  rect(d,size,d*2,0)
  // BLANK[2] 十字を完成させよう
}

