}

// 心臓の鼓動のようなアニメーション
const cycle = 100; // 1周期のフレーム数
let count; // 何フレーム目か
let size=50;
let speed = 1;
function setup(){
  createCanvas(200, 200);
  count = 0;
}

function draw(){
  background(160, 192, 255);
  
   // アニメーションの速さ
  // BLANK[2]
  count = (count + speed) % cycle;
 if(count<cycle/2){
  size+=1
 } 
 else{
  size-=1
 }// BLANK[1] 1周期の前半は size が大きくなり、後半は小さくなる
  ellipse(width / 2, height / 2, size);
  if(keyIsDown("A".charCodeAt(0))){ 
  count = (count + speed+1) % cycle;; }
}

// 2D アニメーションゲームのようなインタラクション
let x, y;
let vx, vy;
const g = 1;

function setup(){
  createCanvas(windowWidth, windowHeight);
  x = width / 2;
  y = height / 2;
  vx = 0;
  vy = 0;
}

function windowResized(){
  resizeCanvas(windowWidth, windowHeight);
}

function draw(){
  background(160, 192, 255);
  const size = height * 0.1; // キャラクターのサイズ

  // 地面を描く
  const groundY = height * 0.8;
  fill(64, 192, 64);
  rect(0, groundY, width, height - groundY);
  
  if(keyIsDown(LEFT_ARROW)){ x -= 5; }
  if(keyIsDown(RIGHT_ARROW)){ x += 5; }
  
  if(keyIsDown("A".charCodeAt(0))){ 
  if(keyIsDown(LEFT_ARROW)){ x -= 5+1; }
  if(keyIsDown(RIGHT_ARROW)){ x += 5+1; }
  }
  // BLANK[1] キャラクターの左右移動

  // BLANK[2] 重力とジャンプ
  if(y<=groundY-size/2){ vy+=g}
  else{
    vy=0
    if(keyIsDown(" ".charCodeAt(0))){ 
    vy}
  }
  // 速くなりすぎないように制限
  vx = constrain(vx, -20, 20);
  vy = constrain(vy, -20, 20);

  // 位置を更新
  x += vx;
  y += vy;

  // キャラクターを描く
  fill(0);
  ellipse(x, y, size, size);
}

